mapper.select:
  		SELECT * FROM 
			(SELECT ROWNUM R, M.* FROM 
				(
					SELECT * 
					FROM NOTICE 
					ORDER BY BOARDNUM DESC
				 ) M
			 )
		WHERE R BETWEEN #{startNum} AND #{endNum}

mapper.update:
UPDATE NOTICE SET
	BOARDTITLE=#{boardTitle},
	boardContent=#{boardContent}
WHERE BOARDNUM =#{boardNum}
